mapper.select:
select a.custcntt_id,a.cnntlist_id,a.prjprepsn_id,a.prj_id,c.prj_name,d.custpsn_id, concat(e.bp_short,' | ',d.custpsn_name) as custpsn_name,a.sales, b.DISPLAY_NAME as salesName,a.contactdate,a.psn_id,f.psn_name,a.price, a.cntt_type,(select V from WB_KEY f where f.KEY_NAME = 'cntt_type' and f.K = a.cntt_type) as cnttTypeName, a.result,(select V from WB_KEY g where g.KEY_NAME = 'OKNG' and g.K = a.result) as resultName, a.resumelvl,(select V from WB_KEY h where h.KEY_NAME = 'resumelvl' and h.K = a.resumelvl) as resumelvlName, a.workhours,a.address,a.cnntime,a.psncnttmemo,a.createtime,a.createuser,a.updatetime,a.updateuser from neg_custcntt a left join WB_USER b on a.sales = b.USER_NAME left join neg_project c on a.prj_id = c.prj_id left join neg_person f on a.psn_id = f.psn_id left join neg_custpsn d on a.custpsn_id = d.custpsn_id left join neg_bp e on d.bpid = e.bpid where a.delflg=0
and e.bpid = #{company}
and a.sales = #{sales}
and a.cntt_type = #{type}
and a.prj_id = #{prj}
and a.custpsn_id = #{custpsn}
and a.psn_id = #{person}
and DATE_FORMAT(a.contactdate,'%Y%m%d') > #{stardate}
and DATE_FORMAT(a.contactdate,'%Y%m%d') < #{enddate}
ORDER BY a.updatetime,a.createtime DESC

select a.bpid,a.bp_code, a.bp_name, a.bp_short, a.empcnt, a.country, a.bpimpt, a.sales, b.DISPLAY_NAME as salesName, a.hpurl, a.zipcode, a.address, a.tel, a.fax, a.createtime, a.createuser, a.updateuser, a.updatetime, a.bp_memo, a.contactdate, a.contactcnt, e.custpsn_cnt,e.psn_cnt,e.kmcnt,e.v_prjcnt,e.v_fzcnt,e.v_tgcnt,e.tacnt, a.maxcnttdays, a.prjprsncnt, a.gotprjcnt, a.bpprsncnt, a.prjflg, a.wngtext, a.wnglvl, a.cnttwnglvl, (select V from WB_KEY c where c.KEY_NAME = 'bpimpt' and c.K = a.bpimpt) as bpimptName, (select V from WB_KEY d where d.KEY_NAME = 'wnglvl' and d.K = a.wnglvl) as wnglvlName neg_bp a left join WB_USER b on a.sales = b.USER_NAME left join negvw_bp e on a.bpid = e.bpid where a.delflg=0
and a.bpid = #{company}
and a.sales = #{sales}
ORDER BY a.updatetime,a.createtime DESC

select a.custpsn_id,a.custpsn_name,concat(c.bp_short,' | ',c.bp_name) as bp_name,c.sales as bpSales,e.DISPLAY_NAME as bpSalesName,b.DISPLAY_NAME as salesName,a.birthday, a.old,a.psn_station,a.psn_tel1,a.psn_tel2,a.email,a.psn_wxid,a.cardfile,a.custpsn_memo,a.contactdate,a.contactcnt,a.maxcnttdays,a.prjflg,a.wngtext, h.custpsn_cnt,h.psn_cnt,h.kmcnt,h.v_prjcnt,h.v_fzcnt,h.v_tgcnt,h.tacnt, a.wnglvl,(select V from WB_KEY j where j.KEY_NAME = 'wnglvl' and j.K = a.wnglvl) as wnglvlName, a.cnttwnglvl,a.bpid,a.position,a.sales,a.createtime,a.createuser,a.updateuser,a.updatetime, a.country,(select V from WB_KEY h where h.KEY_NAME = 'country' and h.K = a.country) as countryName, a.sex,(select V from WB_KEY i where i.KEY_NAME = 'sex' and i.K = a.sex) as sexName from neg_custpsn a left join WB_USER b on a.sales = b.USER_NAME left join negvw_bp h on a.bpid = h.bpid left join neg_bp c on a.bpid = c.bpid left join WB_USER e on c.sales = e.USER_NAME where a.delflg=0
and a.bpid = #{company}
and a.sales = #{sales}
and a.custpsn_name LIKE '%${custpsnName}%'
ORDER BY a.updatetime,a.createtime DESC

mapper.update:
update neg_bp set
delflg = '1'
where bpid = #{bpid}

update neg_custcntt set
delflg = '1'
where custcntt_id = #{id}

update neg_custpsn set
delflg = '1'
where custpsn_id = #{id}
